    }
];

export default function Home() {

    const { cart, setCart } = useAppContext();

    return (
        <div className="px-5 ">
            <h2 className="text-center py-3 bg-light my-1">AGRICULTURE PRODUCTS</h2>
            <div className="row">
                {products.map((product) => (
                    <div className="col-md-3 my-3" key={product.id}>
                        <div className="card">
